for(var buttons=document.getElementsByClassName("button"),display=document.getElementById("display"),operator=null,operand1=0,operand2=null,ans=0,i=0;i<buttons.length;i++)buttons[i].addEventListener("click",(function(){var value=this.getAttribute("value");if("+"==value||"-"==value||"*"==value||"/"==value||"%"==value)operator=value,operand1=parseFloat(display.textContent),ans=operand1+" "+operator,display.innerText=" ";else if("="==value){operand2=parseFloat(display.textContent);var result=eval(ans+" "+operand2);display.innerText=result}else if("AC"==value){var operator=null,operand1=0,operand2=null;display.innerText=" "}else if("backspace"==value){console.log(value);var string=display.textContent,len=string.length;string=string.substr(0,len-1),console.log(string),display.innerText=string}else display.innerText+=value}));