class ToggleLike{constructor(t){this.toggler=t,this.toggleLike()}toggleLike(){$(this.toggler).click((function(t){t.preventDefault();let e=this;$.ajax({type:"POST",url:$(e).attr("href")}).done((function(t){let a=parseInt($(e).attr("data-likes")),s=$(e).attr("data-id");1==t.data.deleted?(a-=1,$(e).html(`<span id="toggle-i-${s}">${a}&nbsp;&nbsp;<i class="far fa-heart"></span>`)):(a+=1,$(e).html(`<span id="toggle-i-${s}">${a}&nbsp;&nbsp;<i class="fas fa-heart"></span>`)),$(e).attr("data-likes",a)})).fail((function(t){console.log("error in completing the request")}))}))}}